// Dados dos banners do HeroBanner
export const bannerData = [
    {
        id: 1,
        image: "https://www.lenovo.com/medias/lenovo-promo-1.jpg", // Substitua pelo URL real da imagem oficial
        alt: "Promoção Lenovo",
        headline: "Descubra as Ofertas Exclusivas",
        subhead: "Tecnologia de ponta para você",
        ctaText: "Ver ofertas",
        ctaLink: "/ofertas",
        logo: "https://www.lenovo.com/medias/lenovo-logo.png"
    },
    {
        id: 2,
        image: "https://www.lenovo.com/medias/lenovo-promo-2.jpg",
        alt: "Notebook Lenovo",
        headline: "Notebook Lenovo Ideapad",
        subhead: "Performance e estilo",
        ctaText: "Saiba mais",
        ctaLink: "/notebooks",
        logo: "https://www.lenovo.com/medias/lenovo-logo.png"
    },
    {
        id: 3,
        image: "https://www.lenovo.com/medias/lenovo-promo-3.jpg",
        alt: "Lenovo Gamer",
        headline: "Linha Gamer Legion",
        subhead: "Poder para vencer",
        ctaText: "Confira",
        ctaLink: "/gamer",
        logo: "https://www.lenovo.com/medias/lenovo-logo.png"
    }
];

// Funções utilitárias para banners
export const getBannerById = (id) => {
    return bannerData.find(banner => banner.id === id);
};

export const getActiveBanners = () => {
    // Aqui você pode adicionar lógica para filtrar banners ativos
    // Por exemplo, baseado em datas ou status
    return bannerData;
};

export const getBannersByCategory = (category) => {
    // Aqui você pode filtrar banners por categoria
    return bannerData.filter(banner =>
        banner.subhead.toLowerCase().includes(category.toLowerCase())
    );
};
