import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const bannerSlides = [
    {
        src: "https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=900&q=80",
        alt: "Banner 1",
        title: "Promoção Especial",
        description: "Aproveite descontos exclusivos em toda a loja!"
    },
    {
        image: 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=900&q=80',
        alt: "Banner 2",
        title: 'Novos Produtos',
        description: 'Confira os lançamentos Lenovo para 2025.'
    },
    {
        image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=900&q=80',

        title: 'Suporte Premium',
        description: 'Tenha atendimento personalizado para seu negócio.'

    }
];

const categories = [
    { icon: '💸', label: 'Promoções' },
    { icon: '💻', label: 'PCs & Tablets' },
    { icon: '📱', label: 'Smartphones' },
    { icon: '🗄️', label: 'Servidores e Armazenamento' },
    { icon: '🔧', label: 'Serviços' },
    { icon: '🎧', label: 'Suporte' },
    { icon: '🏷️', label: 'Outlet' },
];

const profiles = [
    {
        title: 'Lenovo Pro para Negócios',
        description: 'Inscreva-se gratuitamente, obtenha benefícios exclusivos e atendimento personalizado.',
        button: 'Saiba Mais',
        image: 'https://via.placeholder.com/300x180?text=Negócios'
    },
    {
        title: 'Gaming',
        description: 'Jogue de forma mais inteligente com a Lenovo.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=Gaming'
    },
    {
        title: 'Educação',
        description: 'O futuro precisa de você, libere sua paixão e construa um mundo melhor.',
        button: 'Compre Agora',
        image: 'https://via.placeholder.com/300x180?text=Educação'
    }
];

const Home = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => setCurrentSlide((currentSlide + 1) % bannerSlides.length);
    const prevSlide = () => setCurrentSlide((currentSlide - 1 + bannerSlides.length) % bannerSlides.length);

    // Troca automática do banner a cada 4 segundos
    useEffect(() => {
        const interval = setInterval(nextSlide, 4000);
        return () => clearInterval(interval);
    }, [currentSlide]);

    return (
        <div>
            <Header />
            <main>
                {/* Banner em slide */}
                <section style={{ position: 'relative', background: '#222', color: '#fff', padding: '0', textAlign: 'center' }}>
                    <img
                        src={bannerSlides[currentSlide].img || bannerSlides[currentSlide].src}
                        alt={bannerSlides[currentSlide].title}
                        style={{ width: '100%', maxHeight: '300px', objectFit: 'cover', borderRadius: '0 0 16px 16px' }}
                    />
                    <div style={{ position: 'absolute', top: '30px', left: '50%', transform: 'translateX(-50%)', background: 'rgba(0,0,0,0.5)', padding: '16px 32px', borderRadius: '12px' }}>
                        <h2>{bannerSlides[currentSlide].title}</h2>
                        <p>{bannerSlides[currentSlide].description}</p>
                    </div>
                </section>

                {/* Categorias */}
                <section style={{ background: '#fff', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Explore a Lenovo</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {categories.map(cat => (
                            <div key={cat.label} style={{ textAlign: 'center' }}>
                                <div style={{ fontSize: '2rem' }}>{cat.icon}</div>
                                <div>{cat.label}</div>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Perfis de compra */}
                <section style={{ background: '#f9f9f9', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center' }}>Compre de acordo com seu perfil</h3>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '32px', marginTop: '24px' }}>
                        {profiles.map(profile => (
                            <div key={profile.title} style={{ background: '#fff', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', width: '300px', padding: '16px', textAlign: 'left' }}>
                                <img src={profile.image} alt={profile.title} style={{ width: '100%', borderRadius: '8px' }} />
                                <h4>{profile.title}</h4>
                                <p>{profile.description}</p>
                                <button style={{ background: '#fff', color: '#e60012', border: '1px solid #e60012', padding: '8px 16px', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer' }}>
                                    {profile.button}
                                </button>
                            </div>
                        ))}
                    </div>
                </section>
            </main>
            <Footer />
        </div>
    );
};

export default Home;
