import React from 'react';
import { Link } from 'react-router-dom';
import { FaTag, FaClock } from 'react-icons/fa';
import { getActivePromotions } from '../data/lenovoData';
import '../styles/PromotionsBanner.css';

const PromotionsBanner = () => {
    const activePromotions = getActivePromotions();

    if (activePromotions.length === 0) {
        return null;
    }

    return (
        <section className="promotions-banner">
            <div className="container">
                <h2>Ofertas Especiais</h2>
                <div className="promotions-grid">
                    {activePromotions.map((promotion) => (
                        <div key={promotion.id} className="promotion-card">
                            <div className="promotion-image">
                                <img src={promotion.banner} alt={promotion.name} />
                                <div className="promotion-badge">
                                    <FaTag />
                                    <span>{promotion.discount}</span>
                                </div>
                            </div>
                            <div className="promotion-content">
                                <h3>{promotion.name}</h3>
                                <p>{promotion.description}</p>
                                <div className="promotion-meta">
                                    <div className="promotion-categories">
                                        {promotion.categories.map((cat, index) => (
                                            <span key={index} className="category-tag">
                                                {cat}
                                            </span>
                                        ))}
                                    </div>
                                    <div className="promotion-validity">
                                        <FaClock />
                                        <span>Válido até {new Date(promotion.validUntil).toLocaleDateString('pt-BR')}</span>
                                    </div>
                                </div>
                                <Link to={`/promocoes/${promotion.id}`} className="promotion-button">
                                    Ver Ofertas
                                </Link>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default PromotionsBanner;
