import React from 'react';
import ProductList from '../components/ProductList';
import Header from '../components/Header';
import Footer from '../components/Footer';

const Home = () => {
    const featuredProducts = [
        {
            id: 1,
            name: 'Lenovo Yoga Slim 7i',
            description: 'Notebook ultrafino com Intel Core i7 e tela 2.8K OLED',
            price: 6999.99,
            image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-yoga-slim-7-carbon-13-intel-subseries-hero.png'
        },
        // ... outros produtos
    ];

    return (
        <>
            <Header />
            <main>
                {/* Seção de banners ou promoções pode ser adicionada aqui */}

                <section style={{ background: '#fff', padding: '32px 0' }}>
                    <h3 style={{ textAlign: 'center', marginBottom: '24px' }}>Destaques</h3>
                    <ProductList products={featuredProducts} />
                </section>
            </main>
            <Footer />
        </>
    );
};

export default Home;
