import { Link } from 'react-router-dom';

const Cart = () => {
    const cartItems = [
        {
            id: 1,
            name: 'Lenovo Yoga Slim 7i',
            price: 6999.99,
            quantity: 1,
            image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-yoga-slim-7-carbon-13-intel-subseries-hero.png'
        }
    ];

    const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    const shipping = subtotal > 2000 ? 0 : 99.99;
    const total = subtotal + shipping;

    return (
        <div style={{
            maxWidth: '1200px',
            margin: '0 auto',
            padding: '32px 16px',
            display: 'flex',
            gap: '32px'
        }}>
            {/* ... (restante do JSX permanece igual) ... */}
        </div>
    );
};

