import React from 'react';
import '../styles/main.css';

const Footer = () => {
    const footerLinks = [
        {
            title: 'Comprar',
            links: [
                { text: 'Notebooks', url: '/notebooks' },
                { text: 'Desktops', url: '/desktops' },
                { text: 'Workstations', url: '/workstations' },
                { text: 'Tablets', url: '/tablets' },
                { text: 'Smartphones', url: '/smartphones' },
                { text: 'Acessórios', url: '/acessorios' }
            ]
        },
        {
            title: 'Serviços',
            links: [
                { text: 'Lenovo Services', url: '/servicos' },
                { text: 'Suporte Técnico', url: '/suporte' },
                { text: 'Garantia Estendida', url: '/garantia' },
                { text: 'Reciclagem', url: '/reciclagem' }
            ]
        },
        {
            title: 'Sobre a Lenovo',
            links: [
                { text: 'Nossa Empresa', url: '/sobre' },
                { text: 'Sustentabilidade', url: '/sustentabilidade' },
                { text: 'Diversidade e Inclusão', url: '/diversidade' },
                { text: 'Trabalhe Conosco', url: '/carreiras' }
            ]
        },
        {
            title: 'Ajuda',
            links: [
                { text: 'Central de Ajuda', url: '/ajuda' },
                { text: 'Status do Pedido', url: '/pedidos' },
                { text: 'Frete e Entrega', url: '/frete' },
                { text: 'Devoluções', url: '/devolucoes' }
            ]
        }
    ];

    return (
        <footer style={{ background: '#333', color: '#fff', padding: '48px 32px' }}>
            <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
                gap: '32px', 
                maxWidth: '1200px', 
                margin: '0 auto',
                paddingBottom: '32px',
                borderBottom: '1px solid #555'
            }}>
                {footerLinks.map((section, index) => (
                    <div key={index}>
                        <h3 style={{ fontSize: '1.1rem', marginBottom: '16px' }}>{section.title}</h3>
                        <ul style={{ listStyle: 'none', padding: 0 }}>
                            {section.links.map((link, idx) => (
                                <li key={idx} style={{ marginBottom: '8px' }}>
                                    <a 
                                        href={link.url} 
                                        style={{ 
                                            color: '#ccc', 
                                            textDecoration: 'none',
                                            transition: 'color 0.2s'
                                        }}
                                        onMouseOver={(e) => e.target.style.color = '#fff'}
                                        onMouseOut={(e) => e.target.style.color = '#ccc'}
                                    >
                                        {link.text}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
            <div style={{ 
                maxWidth: '1200px', 
                margin: '32px auto 0', 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                flexWrap: 'wrap',
                gap: '16px'
            }}>
                <p style={{ margin: 0 }}>&copy; {new Date().getFullYear()} Lenovo. Todos os direitos reservados.</p>
                <div style={{ display: 'flex', gap: '24px' }}>
                    <a href="/termos" style={{ color: '#ccc', textDecoration: 'none' }}>Termos de Uso</a>
                    <a href="/privacidade" style={{ color: '#ccc', textDecoration: 'none' }}>Política de Privacidade</a>
                    <a href="/cookies" style={{ color: '#ccc', textDecoration: 'none' }}>Cookies</a>
                </div>
            </div>
        </footer>
    );
};

export default Footer;
