import React from 'react';
import { Link } from 'react-router-dom';
import { FaArrowRight, FaTag, FaLaptop, FaMobile, FaServer, FaHeadset, FaCheckCircle, FaUsers, FaGamepad, FaGraduationCap, FaCar, FaWind } from 'react-icons/fa';
import { lenovoData, getActivePromotions } from '../data/lenovoData';
import '../styles/Home.css';

const Home = () => {
    const exploreCategories = lenovoData.categories.map(cat => {
        const iconMap = {
            laptop: FaLaptop,
            desktop: FaServer,
            tablet: FaMobile,
            mobile: FaMobile,
            server: FaServer,
            gamepad: FaGamepad,
            headset: FaHeadset,
            support: FaCheckCircle
        };

        return {
            name: cat.name,
            icon: iconMap[cat.icon] || FaLaptop,
            link: `/produtos/${cat.id}`,
            description: cat.description
        };
    });

    const profileCards = [
        {
            title: "Lenovo Pro para Negócios",
            description: "Inscreva-se para ter acesso a benefícios exclusivos e atendimento personalizado.",
            image: "https://images.unsplash.com/photo-1552664730-d307ca884978?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
            buttonText: "Saiba Mais",
            link: "/negocios"
        },
        {
            title: "Gaming",
            description: "Jogue de forma mais inteligente com a Lenovo.",
            image: "https://images.unsplash.com/photo-1542751371-adc38448a05e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
            buttonText: "Compre Agora",
            link: "/gaming"
        },
        {
            title: "Educação",
            description: "O futuro precisa de você. Libere seu potencial e construa um mundo melhor.",
            image: "https://images.unsplash.com/photo-1523240794102-9ebd8b0c4b5d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
            buttonText: "Compre Agora",
            link: "/educacao"
        }
    ];

    const potentialCards = [
        {
            title: "Parceria Inovadora",
            description: "Redefinindo as possibilidades com nossa tecnologia.",
            image: "https://images.unsplash.com/photo-1563013544-824ae1b704d3?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
            buttonText: "Saiba Mais",
            link: "/parceria"
        },
        {
            title: "Nosso Impacto",
            description: "Comprometidos com a responsabilidade social.",
            image: "https://images.unsplash.com/photo-1466611653911-95081537e5b7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
            buttonText: "Saiba Mais",
            link: "/impacto"
        }
    ];

    return (
        <div className="home">
            {/* Hero Section */}
            <section className="hero">
                <div className="hero-image">
                    <img src="https://images.unsplash.com/photo-1449824913935-59a10b8d2000?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80" alt="Lenovo City View" />
                    <div className="hero-overlay">
                        <div className="hero-text">
                            <p className="hero-subtitle">SOMOS LENOVO</p>
                            <h1 className="hero-title">Nossa História</h1>
                            <button className="hero-button">Saiba Mais</button>
                        </div>
                    </div>
                    <div className="hero-carousel">
                        <span className="carousel-dot active"></span>
                        <span className="carousel-dot"></span>
                        <span className="carousel-dot"></span>
                    </div>
                    <button className="carousel-arrow carousel-prev">‹</button>
                    <button className="carousel-arrow carousel-next">›</button>
                </div>
            </section>

            {/* Explore Lenovo Section */}
            <section className="explore-lenovo">
                <div className="container">
                    <h2>Explore a Lenovo</h2>
                    <div className="explore-grid">
                        {exploreCategories.map((category, index) => (
                            <Link key={index} to={category.link} className="explore-item">
                                <div className="explore-icon">
                                    <category.icon />
                                </div>
                                <span>{category.name}</span>
                            </Link>
                        ))}
                    </div>
                </div>
            </section>

            {/* Profile Section */}
            <section className="profile-section">
                <div className="container">
                    <h2>Compre de acordo com seu perfil</h2>
                    <div className="profile-grid">
                        {profileCards.map((card, index) => (
                            <div key={index} className="profile-card">
                                <div className="profile-image">
                                    <img src={card.image} alt={card.title} />
                                </div>
                                <div className="profile-content">
                                    <h3>{card.title}</h3>
                                    <p>{card.description}</p>
                                    <Link to={card.link} className="profile-button">
                                        {card.buttonText}
                                    </Link>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Potential Section */}
            <section className="potential-section">
                <div className="container">
                    <h2>Aumente seu potencial por meio da tecnologia</h2>
                    <p className="potential-subtitle">Nossos valores</p>
                    <div className="potential-grid">
                        {potentialCards.map((card, index) => (
                            <div key={index} className="potential-card">
                                <div className="potential-image">
                                    <img src={card.image} alt={card.title} />
                                </div>
                                <div className="potential-content">
                                    <h3>{card.title}</h3>
                                    <p>{card.description}</p>
                                    <Link to={card.link} className="potential-button">
                                        {card.buttonText}
                                    </Link>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Home;
