import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HeroBanner.css';

const HeroBanner = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [isPaused, setIsPaused] = useState(false);
    const [progress, setProgress] = useState(0);
    const intervalRef = useRef(null);
    const progressRef = useRef(null);

    const slides = [
        {
            id: 0,
            image: "https://p2-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-mwc-2025-hpb-teaser-desktop.jpg",
            logo: "https://p1-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-mwc-2025-wordmark-white.png",
            subhead: "IA MAIS INTELIGENTE PARA TODOS",
            headline: "A Lenovo libera o poder da IA pessoal e empresarial para construir um futuro mais inteligente para todos.",
            ctaText: "Veja Contagem regressiva",
            ctaLink: "https://www.lenovo.com/br/pt/mwc",
            alt: "Lenovo MWC 2025"
        },
        {
            id: 1,
            image: "https://p3-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-fifa-2024-hpb-desktop.jpg",
            logo: "https://p3-ofp.static.pub/ShareResource/ww/img/homepage-banners/lenovo-fifa-logo-lockup.png",
            subhead: "PARCEIRO OFICIAL DE TECNOLOGIA DA FIFA™",
            headline: "A Lenovo tem orgulho de ser a parceira tecnológica global oficial da FIFA World Cup 26™ e da FIFA Women's World Cup 2027™.",
            ctaText: "Saiba Mais",
            ctaLink: "https://news.lenovo.com/pressroom/press-releases/official-fifa-technology-partner-tw2024?lg=pt-br",
            alt: "As taças da copa do mundo 26 masculina e da copa do mundo 27 feminina no meio do campo de futebol ao lado de um portal vermelho"
        },
        {
            id: 2,
            image: "https://p2-ofp.static.pub/ShareResource/na/ftv/hero/lenovo-new-dawn.jpg",
            subhead: "Somos Lenovo",
            headline: "Nossa História.",
            ctaText: "Saiba Mais",
            ctaLink: "https://www.lenovo.com/br/pt/about/whoweare/",
            alt: "Nós somos a Lenovo"
        }
    ];

    const SLIDE_DURATION = 5000; // 5 segundos

    const startAutoplay = () => {
        if (intervalRef.current) clearInterval(intervalRef.current);
        
        intervalRef.current = setInterval(() => {
            if (!isPaused) {
                setCurrentSlide((prev) => (prev + 1) % slides.length);
                setProgress(0);
            }
        }, SLIDE_DURATION);
    };

    const pauseAutoplay = () => {
        setIsPaused(true);
    };

    const resumeAutoplay = () => {
        setIsPaused(false);
    };

    useEffect(() => {
        startAutoplay();
        return () => {
            if (intervalRef.current) clearInterval(intervalRef.current);
        };
    }, [isPaused]);

    // Progress bar animation
    useEffect(() => {
        if (progressRef.current) clearInterval(progressRef.current);
        
        if (!isPaused) {
            const startTime = Date.now();
            progressRef.current = setInterval(() => {
                const elapsed = Date.now() - startTime;
                const newProgress = (elapsed / SLIDE_DURATION) * 100;
                setProgress(Math.min(newProgress, 100));
            }, 50);
        }

        return () => {
            if (progressRef.current) clearInterval(progressRef.current);
        };
    }, [currentSlide, isPaused]);

    const goToSlide = (index) => {
        setCurrentSlide(index);
        setProgress(0);
        pauseAutoplay();
        setTimeout(resumeAutoplay, 3000); // Resume after 3 seconds
    };

    const goToPrevSlide = () => {
        setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
        setProgress(0);
        pauseAutoplay();
        setTimeout(resumeAutoplay, 3000);
    };

    const goToNextSlide = () => {
        setCurrentSlide((prev) => (prev + 1) % slides.length);
        setProgress(0);
        pauseAutoplay();
        setTimeout(resumeAutoplay, 3000);
    };

    const handleMouseEnter = () => {
        pauseAutoplay();
    };

    const handleMouseLeave = () => {
        resumeAutoplay();
    };

    return (
        <div 
            className="hero-banner"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            role="region"
            aria-label="Banner principal"
        >
            <div className="swiper-wrapper" style={{ transform: `translate3d(-${currentSlide * 100}%, 0px, 0px)` }}>
                {slides.map((slide, index) => (
                    <div
                        key={slide.id}
                        className={`swiper-slide hero_slide ${index === currentSlide ? 'swiper-slide-active' : ''}`}
                        data-swiper-slide-index={index}
                        aria-hidden={index !== currentSlide}
                    >
                        <div className="mask_smb_link">
                            <a 
                                aria-label={slide.ctaText} 
                                href={slide.ctaLink} 
                                target="_self"
                                rel="noopener noreferrer"
                            ></a>
                        </div>
                        <div className="hero_slide_img">
                            <img 
                                src={slide.image} 
                                alt={slide.alt} 
                                loading={index === 0 ? "eager" : "lazy"}
                                onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.nextSibling.style.display = 'block';
                                }}
                            />
                            <div 
                                className="hero_slide_fallback" 
                                style={{ 
                                    display: 'none',
                                    width: '100%',
                                    height: '100%',
                                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                    position: 'absolute',
                                    top: 0,
                                    left: 0
                                }}
                            >
                                <div style={{
                                    position: 'absolute',
                                    top: '50%',
                                    left: '50%',
                                    transform: 'translate(-50%, -50%)',
                                    color: 'white',
                                    textAlign: 'center'
                                }}>
                                    <h2>{slide.subhead}</h2>
                                    <p>{slide.headline}</p>
                                </div>
                            </div>
                        </div>
                        <div className="hero_outer">
                            <div className="hero_text">
                                {slide.logo && (
                                    <div>
                                        <img 
                                            loading="lazy" 
                                            className="hero_outer_img" 
                                            src={slide.logo} 
                                            alt="Logo" 
                                            onError={(e) => {
                                                e.target.style.display = 'none';
                                            }}
                                        />
                                    </div>
                                )}
                                <div className="hero_text_subhead">
                                    <p>{slide.subhead}</p>
                                </div>
                                <div className="hero_text_headline" role="heading" aria-level="2">
                                    <p>{slide.headline}</p>
                                </div>
                            </div>
                            <div className="hero_cta">
                                <div className="hero_cta_inner">
                                    <a 
                                        href={slide.ctaLink} 
                                        target="_self" 
                                        aria-label={slide.ctaText}
                                        rel="noopener noreferrer"
                                    >
                                        <div className="hero_cta_btn">{slide.ctaText}</div>
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Progress bar */}
            <div className="progress-bar">
                <div 
                    className="progress-fill" 
                    style={{ width: `${progress}%` }}
                ></div>
            </div>

            {/* Navigation arrows */}
            <button 
                className="carousel-arrow carousel-prev" 
                onClick={goToPrevSlide}
                aria-label="Slide anterior"
            >
                ‹
            </button>
            <button 
                className="carousel-arrow carousel-next" 
                onClick={goToNextSlide}
                aria-label="Próximo slide"
            >
                ›
            </button>

            {/* Dots navigation */}
            <div className="hero-carousel" role="tablist" aria-label="Navegação do banner">
                {slides.map((_, index) => (
                    <button
                        key={index}
                        className={`carousel-dot ${index === currentSlide ? 'active' : ''}`}
                        onClick={() => goToSlide(index)}
                        aria-label={`Ir para slide ${index + 1}`}
                        aria-selected={index === currentSlide}
                        role="tab"
                    ></button>
                ))}
            </div>

            {/* Slide counter */}
            <div className="slide-counter" aria-live="polite">
                {currentSlide + 1} / {slides.length}
            </div>
        </div>
    );
};

export default HeroBanner;
