import ProductList from '../components/ProductList';

// Adicione esta constante antes do componente Home
const featuredProducts = [
    {
        id: 1,
        name: 'Lenovo Yoga Slim 7i',
        description: 'Notebook ultrafino com Intel Core i7 e tela 2.8K OLED',
        price: 6999.99,
        image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-yoga-slim-7-carbon-13-intel-subseries-hero.png'
    },
    {
        id: 2,
        name: 'Lenovo Legion Pro 7i',
        description: 'Notebook gamer com NVIDIA RTX 4080 e Intel Core i9',
        price: 15999.99,
        image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-legion-pro-7-16-intel-subseries-hero.png'
    },
    {
        id: 3,
        name: 'ThinkPad X1 Carbon Gen 11',
        description: 'Notebook empresarial premium com tela 2.2K',
        price: 8999.99,
        image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-thinkpad-x1-carbon-gen-11-subseries-hero.png'
    },
    {
        id: 4,
        name: 'IdeaPad Gaming 3i',
        description: 'Notebook gamer acessível com NVIDIA RTX 3050',
        price: 4999.99,
        image: 'https://p3-ofp.static.pub/ShareResource/na/subseries/hero/lenovo-laptop-ideapad-gaming-3-15-intel-subseries-hero.png'
    }
];

// No return do componente Home, adicione esta seção antes do Footer
<section style={{ background: '#fff', padding: '32px 0' }}>
    <h3 style={{ textAlign: 'center', marginBottom: '24px' }}>Destaques</h3>
    <ProductList products={featuredProducts} />
</section>
